//? 문제 : 최솟값 구하기

//? 7개의 수가 주어지면 그 숫자 중 가장 작은 수를 출력

const solution = (arr) => {
  let answer = Number.MAX_SAFE_INTEGER;

  for (const i of arr) {
    if (i < answer) answer = i;
  }

  return answer;
};

let arr = [5, 3, 7, 11, 2, 15, 17];
console.log(solution(arr)); // 2;

//? 영상 풀이
const solution2 = (arr) => {
  let answer,
    min = Number.MAX_SAFE_INTEGER;

  for (let i = 0; i < arr.length; i++) {
    if (arr[i] < min) min = arr[i];
  }
  answer = min;
  return answer;
};

arr = [5, 7, 1, 3, 2, 9, 11];
console.log(solution2(arr)); // 1

//? 영상 풀이 : 내장함수 사용
//* ... : 전개 구문
const solution3 = (arr) => {
  return Math.min(...arr);

  //* 전개구문 없이 하는 방법
  // Math.min.apply(null, arr)
};

console.log(solution3(arr));
